mapper.select:
SELECT

FROM sys_area a

WHERE a.id = #{id}

SELECT

FROM sys_area a




	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.parent_id LIKE #{parent.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.tree_level ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM sys_area a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY ${page.orderBy}
ORDER BY a.sort ASC

mapper.update:
UPDATE sys_area SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
